(async() => {
    var json1 = await http(`http://android.jjwxc.net/androidapi/search?keyword=${keyword}&type=1&page=${page}&searchType=1&sortMode=DESC`);
    var json2 = await http(`http://android.jjwxc.net/androidapi/search?keyword=${keyword}&type=2&page=${page}&searchType=1&sortMode=DESC`);
    var item1 = JSON.parse(json1).items ?? [];
    var item2 = JSON.parse(json2).items ?? [];
    return item1.concat(item2);
})();
