contentUrl:
@js:
(async ()=>{
const parseUrl = host + "/hls2/index.php?url=";
var pageUrl = host + result;
var html = await http(pageUrl);
var view_path = html.match(/view_path = '([^']*)/)[1];
var playHtml = await http(parseUrl + view_path);
var hdMd5 = await xpath(playHtml, "//input[@id='hdMd5']/@value");
return {
    url: host + '/hls2/url.php',
    method: 'POST',
    body: {
        id: view_path,
        type: 'vid',
        md5: hdMd5.toString(),
        referer: pageUrl
    }
};
})();

